b'# Function to find the maximum number in a list\nfind_max <- function(numbers) {\n    max_num <- max(numbers)\n    return(max_num)\n}\n\n# Function to find the minimum number in a list\nfind_min <- function(numbers) {\n    min_num <- min(numbers)\n    return(min_num)\n}\n\n# Example usage\nnumbers <- c(1, 5, 3, 9, 2)\nmax_num <- find_max(numbers)\nmin_num <- find_min(numbers)\nprint(max_num)\nprint(min_num)' + '\n' + '# Function to find the minimum number in a list\nfind_min <- function(numbers) {\n    min_num <- min(numbers)\n    return(min_num)\n}\n\n# Example usage\nnumbers <- c(1, 5, 3, 9, 2)\nmin_num <- find_min(numbers)\nprint(min_num)'